mapper.delete:
DELETE FROM t_elective


        id = #{id}


        AND stu_id = #{stuId}


        AND course_id = #{courseId}

mapper.update:
UPDATE t_elective
SET stu_id = #{elective.stuId}, course_id = #{elective.courseId}, score = #{elective.score}
WHERE id = #{elective.id}

mapper.select:
SELECT id, stu_id AS stuId, course_id AS courseId, score
FROM t_elective
WHERE id = #{id}

SELECT id, stu_id AS stuId, course_id AS courseId, score
FROM t_elective


        stu_id = #{stuId}


        AND course_id = #{courseId}
